jQuery(document).ready(function(){KINDLYCARE_STORAGE.media_frame=null;KINDLYCARE_STORAGE.media_link="";jQuery(".kindlycare_media_selector").on("click",function(a){kindlycare_show_media_manager(this);a.preventDefault();return false})});function kindlycare_show_media_manager(a){KINDLYCARE_STORAGE.media_link=jQuery(a);if(KINDLYCARE_STORAGE.media_frame){KINDLYCARE_STORAGE.media_frame.open();return false}KINDLYCARE_STORAGE.media_frame=wp.media({title:KINDLYCARE_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:KINDLYCARE_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:KINDLYCARE_STORAGE.media_link.data("update"),close:true}});KINDLYCARE_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+KINDLYCARE_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(KINDLYCARE_STORAGE.media_link.data("multiple")===true){KINDLYCARE_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=KINDLYCARE_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});KINDLYCARE_STORAGE.media_frame.open();return false};